import React, { useRef, useState } from 'react'


import firebase from 'firebase/compat/app';
import 'firebase/compat/firestore';
import 'firebase/compat/auth';

import { useCollectionData } from 'react-firebase-hooks/firestore';

firebase.initializeApp({
    apiKey: "AIzaSyAAOCaIdesO7FSmnC-mwcsgCrCMZ7oGTGQ",
    authDomain: "fir-chat-586df.firebaseapp.com",
    projectId: "fir-chat-586df",
    storageBucket: "fir-chat-586df.appspot.com",
    messagingSenderId: "673113868883",
    appId: "1:673113868883:web:ad4bd84f562d610f7414a6",
    measurementId: "G-3SMN55JTV4"
})

const auth = firebase.auth();
const firestore = firebase.firestore();


export default function ChatRoom(props) {
    const chatId = props.classId;
    // const [chatId, setChatId] = useState(props.classId)

    const dummy = useRef();

    const messagesRef = firestore.collection(`${chatId}`);
    const query = messagesRef.orderBy('createdAt').limit(200);

    const [messages] = useCollectionData(query, { idField: 'id' });

    const [formValue, setFormValue] = useState('');

    const sendMessage = async (e) => {
        e.preventDefault();

        const { uid, photoURL } = auth.currentUser;

        await messagesRef.add({
            text: formValue,
            createdAt: firebase.firestore.FieldValue.serverTimestamp(),
            uid,
            photoURL
        });

        setFormValue(' ');

        dummy.current.scrollIntoView({ behavior: "smooth" });
    }

    return (
        <div>

            <main>


                <div>
                    {messages && messages.map(msg => <ChatMessage key={msg.id} message={msg} />)}
                </div>

                <div ref={dummy}></div>
                <form className="chat-form " onSubmit={sendMessage}>
                    <input className="chat-input" placeholder="Meet your classmates" value={formValue} onChange={(e) => setFormValue(e.target.value)}>

                    </input>

                    <button type="submit" > Send</button>
                </form>
            </main>

            <div>

            </div>
        </div>

    )
}

function ChatMessage(props) {
    const { text, uid, photoURL } = props.message;

    const messageClass = uid === auth.currentUser.uid ? 'sent' : 'received';

    return (
        <div className={`message ${messageClass}`}>
            <img src={photoURL} />

            <p> {text} </p>

        </div>
    )
}
